"""
Permutation in String

Given two strings s1 and s2, return true if s2 contains a permutation of s1, or false otherwise.
In other words, return true if one of s1's permutations is the substring of s2.


Example 1:
Input: s1 = "ab", s2 = "eidbaooo"
Output: true
Explanation: s2 contains one permutation of s1 ("ba").

Example 2:
Input: s1 = "ab", s2 = "eidboaoo"
Output: false


Constraints:
1 <= s1.length, s2.length <= 104
s1 and s2 consist of lowercase English letters.
"""

class Solution:

    def checkInclusion(self, s1: str, s2: str) -> bool:
        # create map for s1
        s1_map = [0] * 26
        s1_len = len(s1)

        for ch in s1:
            s1_map[ord(ch) - ord('a')] += 1

        # Initialize map for s2
        s2_map = [0] * 26

        left_pointer = 0

        for right_pointer, ch in enumerate(s2):
            s2_map[ord(ch) - ord('a')] += 1

            if (right_pointer - left_pointer  + 1) == s1_len:
                if s1_map == s2_map:
                    return True
                else:
                    s2_map[ord(s2[left_pointer]) - ord('a')] -= 1
                    left_pointer += 1

        return False

if __name__ == "__main__":
    s = Solution()
    assert s.checkInclusion("ab", "eidbaooo") == True
    assert s.checkInclusion("ab", "eidboaoo") == False
    assert s.checkInclusion("adc", "dcda") == True
    assert s.checkInclusion(
        "",
        "") == False
    assert s.checkInclusion(
        "",
        "") == True

